import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";

function Codeforces() {
  const [handle, sethandle] = useState("Hustlingbeast_Anish");
  const [users, setusers] = useState([{}]);

  const fetchdetails = () => {
    axios
      .get(`https://codeforces.com/api/user.info?handles=${handle}`)
      .then((res) => {
        setusers(res.data.result[0]);
        console.log(res.data.result[0]);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <>
      <input
        // type="text"
        placeholder="Enter Your Codeforces Id"
        onChange={(e) => {
          sethandle(e.target.value);
        }}
      />
      <button onClick={fetchdetails}>Find</button>
      <div class="card" style={{ width: "18rem" }}>
        <img src={users.titlePhoto} class="card-img-top" alt="..." />
        <div class="card-body">
          <h5>
            Name: {users.firstName} {users.lastName}
          </h5>
        </div>
      </div>
      <h2>Maximum Rating {users.maxRating}</h2>
    </>
  );
}

export default Codeforces;
