import React from "react";
import { useState, useEffect } from "react";
import axios from "axios";

function Codeforces() {
  const [handle, sethandle] = useState("Hustlingbeast_Anish");
  const [users, setusers] = useState([{}]);

  const fetchdetails = () => {
    axios
      .get(`https://codeforces.com/api/user.info?handles=${handle}`)
      .then((res) => {
        setusers(res.data.result[0]);
        console.log(res.data.result[0]);
      })
      .catch((e) => {
        console.log(e);
      });
  };
  return (
    <>
      <div className="container d-flex mt-5 flex-column align-items-center border border-dark">
        <h2>Get Your Codeforces Details Here</h2>
        <div class="form-group">
          <label for="exampleInputEmail1">CodeForces UserName</label>
          <input
            type="email"
            class="form-control"
            id="exampleInputEmail1"
            aria-describedby="emailHelp"
            placeholder="Enter your Handle Here"
            onChange={(e) => {
              sethandle(e.target.value);
            }}
          />
          <small id="emailHelp" class="form-text text-muted">
            We'll never share your details
          </small>
        </div>

        <button type="button" class="btn btn-primary" onClick={fetchdetails}>
          Find
        </button>
        <div class="card" style={{ width: "20rem" }}>
          <img src={users.titlePhoto} class="card-img-top" alt="..." />
          <div class="card-body">
            <div className="container">
              <h5>
                Name: {users.firstName} {users.lastName}
              </h5>
              <h5>Title: {users.rank}</h5>
              <h5>Name: {users.maxRank}</h5>
              <h5>Rating: {users.rating}</h5>
              <h5>Highest Rating: {users.maxRating}</h5>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}

export default Codeforces;
